package au.csiro.cass.arch.security;

import org.apache.hadoop.conf.Configurable;

/** A threats scanner for content generated by a {@link org.apache.nutch.protocol.Protocol}
 * implementation.  This interface is implemented by extensions. 
 */
public interface Scanner extends Configurable
{
  /** The name of the extension point. */
  public final static String X_POINT_ID = Scanner.class.getName();

  /** 
   * Scans the given content for threats and vulnerabilities
   * 
   * @param context scanning context
   * @param scanResult results of scanning
   */
  void scan( ScanContext context, ScanResult scanResult ) throws Exception ;
}
